import React from "react";
import { Link } from "react-router-dom";
import { apiDelete } from "../api";

export default function EquipmentList({ items, onRequest, userRole }){
  async function handleDelete(id){
    if (!window.confirm("Delete this item?")) return;
    try{
      await apiDelete("/equipment/" + id);
      alert("Deleted");
      window.location.reload();
    }catch(ex){
      alert(ex.data?.error || "Delete failed");
    }
  }

  return (
    <div>
      <table className="table card">
        <thead>
          <tr><th>Name</th><th>Category</th><th>Condition</th><th>Qty</th><th>Available</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {items.length === 0 && <tr><td colSpan="6">No items</td></tr>}
          {items.map(it => (
            <tr key={it.id}>
              <td>{it.name}</td>
              <td>{it.category}</td>
              <td>{it.condition}</td>
              <td>{it.quantity}</td>
              <td>{it.available}</td>
              <td className="actions">
                {userRole === "student" && it.available > 0 && <button onClick={()=>onRequest(it.id)}>Request</button>}
                {userRole === "admin" && <>
                  <Link to={`/equipment/edit/${it.id}`}><button>Edit</button></Link>
                  <button onClick={()=>handleDelete(it.id)}>Delete</button>
                </>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
