import React, { useEffect, useState } from "react";
import { apiGet, apiPut } from "../api";

export default function PendingRequests(){
  const [items, setItems] = useState([]);
  const [err, setErr] = useState("");

  async function load(){
    setErr("");
    try{
      const data = await apiGet("/loans/pending");
      setItems(data);
    }catch(ex){
      setErr(ex.data?.error || "Failed to load");
    }
  }

  useEffect(()=>{ load(); }, []);

  async function approve(id){
    try{
      await apiPut(`/loans/${id}/approve`, {});
      alert("Approved");
      load();
    }catch(ex){
      alert(ex.data?.error || "Approve failed");
    }
  }

  async function reject(id){
    try{
      await apiPut(`/loans/${id}/reject`, {});
      alert("Rejected");
      load();
    }catch(ex){
      alert(ex.data?.error || "Reject failed");
    }
  }

  return (
    <div>
      <h3>Pending Requests</h3>
      {err && <div style={{color:"red"}}>{err}</div>}
      <table className="table card">
        <thead><tr><th>User</th><th>Equipment</th><th>Requested</th><th>Notes</th><th>Actions</th></tr></thead>
        <tbody>
          {items.length === 0 && <tr><td colSpan="5">No pending requests</td></tr>}
          {items.map(it => (
            <tr key={it.id}>
              <td>{it.user_id}</td>
              <td>{it.equipment_id}</td>
              <td>{new Date(it.request_date).toLocaleString()}</td>
              <td>{it.notes}</td>
              <td>
                <button onClick={()=>approve(it.id)}>Approve</button>
                <button onClick={()=>reject(it.id)}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
