import React, { useEffect, useState } from "react";
import { apiGet, apiPost, apiDelete } from "../api";
import { getUser } from "../auth";
import EquipmentList from "./EquipmentList";

export default function Dashboard(){
  const user = getUser();
  const [q, setQ] = useState("");
  const [category, setCategory] = useState("");
  const [availableOnly, setAvailableOnly] = useState(true);
  const [items, setItems] = useState([]);
  const [err, setErr] = useState("");

  async function load(){
    setErr("");
    try{
      const params = new URLSearchParams();
      if (q) params.set("q", q);
      if (category) params.set("category", category);
      if (availableOnly) params.set("available", "true");
      const data = await apiGet("/equipment?" + params.toString());
      setItems(data);
    }catch(ex){
      setErr(ex.data?.error || "Failed to load");
    }
  }

  useEffect(()=>{ load(); }, []);

  async function requestLoan(equipmentId){
    try{
      await apiPost("/loans/request", { equipment_id: equipmentId });
      alert("Request created");
      load();
    }catch(ex){
      alert(ex.data?.error || "Request failed");
    }
  }

  return (
    <div>
      <div className="card topbar">
        <div className="search">
          <input placeholder="Search name..." value={q} onChange={e=>setQ(e.target.value)} />
        </div>
        <div>
          <label style={{marginRight:8}}><input type="checkbox" checked={availableOnly} onChange={e=>setAvailableOnly(e.target.checked)} /> Available only</label>
          <button onClick={load}>Search</button>
        </div>
      </div>

      {err && <div className="card" style={{color:"red"}}>{err}</div>}

      <EquipmentList items={items} onRequest={(id)=>requestLoan(id)} userRole={user?.role} />
    </div>
  );
}
