import React, { useEffect, useState } from "react";
import { apiGet, apiPut } from "../api";
import { getUser } from "../auth";

export default function MyLoans(){
  const [loans, setLoans] = useState([]);
  const [err, setErr] = useState("");
  const user = getUser();

  async function load(){
    setErr("");
    try{
      const data = await apiGet("/loans/my");
      setLoans(data);
    }catch(ex){
      setErr(ex.data?.error || "Failed to load");
    }
  }

  useEffect(()=>{ load(); }, []);

  // Staff/Admin can mark returned; but this page is mostly read-only for students
  async function markReturned(id){
    try{
      await apiPut(`/loans/${id}/return`, {});
      alert("Marked returned");
      load();
    }catch(ex){
      alert(ex.data?.error || "Failed");
    }
  }

  return (
    <div>
      <h3>My Loans</h3>
      {err && <div style={{color:"red"}}>{err}</div>}
      <table className="table card">
        <thead><tr><th>Equipment</th><th>Status</th><th>Requested</th><th>Return Date</th><th>Actions</th></tr></thead>
        <tbody>
          {loans.length === 0 && <tr><td colSpan="5">No loans</td></tr>}
          {loans.map(l => (
            <tr key={l.id}>
              <td>{l.equipment_id}</td>
              <td>{l.status}</td>
              <td>{new Date(l.request_date).toLocaleString()}</td>
              <td>{l.return_date ? new Date(l.return_date).toLocaleString() : "-"}</td>
              <td>
                {(user.role === "staff" || user.role === "admin") && l.status === "APPROVED" && <button onClick={()=>markReturned(l.id)}>Mark Returned</button>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
